createApp({data(){return{isLoading:!0,isNewVersionAvailable:!1,latestVersion:"0.0"}},methods:{async checkVersion(){let n=await axios.get("/api/update/check").catch(n=>{console.log(n.message)});this.isNewVersionAvailable=n.data.isNewVersionAvailable;this.latestVersion=n.data.latestReleaseVersionNumber},async loadStats(){this.isLoading=!0;let n=await axios.get("/api/hosts").catch(n=>{UiHelpers.showErrorToast("Error","",n.message),this.isLoading=!1});this.isLoading=!1}},mounted:function(){UiHelpers.setPageHeader("Dashboard");this.isLoading=!1;this.loadStats();this.checkVersion()}}).mount("#app-dashboard");