const{createApp}=Vue;createApp({data(){return{isLoading:!0,host:new HostModel}},methods:{async submit(){if(this.isLoading=!0,$("#toastsContainerTopRight > .toast").remove(),!this.host.validate()){this.isLoading=!1;return}axios.post("/api/hosts",this.host).then(()=>{window.location.href="/hosts"}).catch(n=>{var i,t;if(n.response&&n.response.data&&n.response.data.errorMessages)for(i=n.response.data.errorMessages,t=0;t<i.length;t++)UiHelpers.showErrorToast("Validation Error","",i[t]);else UiHelpers.showErrorToast("Validation Error","",n.message);this.isLoading=!1})}},mounted:function(){this.isLoading=!1}}).mount("#app-host");