createApp({data(){return{jobs:[],isLoading:!0,selectedJob:new JobModel}},methods:{async deleteJob(n){var t=this;if(this.isLoading=!0,this.selectedJob!=null&&n.length>0){let i=await axios.delete("/api/jobs/"+n).then(()=>{t.loadJobs()}).catch(n=>{UiHelpers.showErrorToast("Error","",n.message),t.isLoading=!1}).then(()=>{$("#modal-delete-job").modal("toggle")})}},formatDateTime(n){return UiHelpers.formatDateTime(n)},async loadJobs(){var n=this;this.isLoading=!0;let t=await axios.get("/api/jobs").catch(t=>{UiHelpers.showErrorToast("Error","",t.message),n.isLoading=!1});this.jobs=t.data;this.isLoading=!1},showDeleteDialog(n){this.selectedJob=n;$("#modal-delete-job").modal()}},mounted:function(){this.isLoading=!1;UiHelpers.setPageHeader("Jobs");this.loadJobs()}}).mount("#app-job");